package com.Acc.jdbc;

public class naveen {
	private int id;
	private String name;
	
	public naveen(int id, String name) {
		this.id=id;
		this.name=name;
		// TODO Auto-generated constructor stub
	}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	

}
